/**
 * 除了预设值，也可以是其他任意自定义颜色值
 */
export type IColor =
  | 'red'
  | 'orange'
  | 'green'
  | 'blue'
  | 'purple'
  | 'brown'
  | 'grey'
  | string

export type IEffect = 'dark' | 'light' | 'plain'

export type ISize = 'medium' | 'small' | 'mini' | ''
export type IType = 'success' | 'info' | 'warning' | 'danger'
